mapper.select:
/* queryId = selectEmpList */
SELECT * 
  FROM USER_INFO_MDJ
 WHERE 1=1

   AND ID = #{id}

/* queryId = selectCountEmp */
SELECT COUNT(*) AS CNT 
  FROM USER_INFO_MDJ
 WHERE ID = #{id}

/* queryId = selectCountEmpPw */
SELECT COUNT(*) AS CNT 
  FROM USER_INFO_MDJ
 WHERE ID = #{id}
   AND PW = #{pw}

mapper.update:
/* queryId = updateFalieCnt */
UPDATE USER_INFO_MDJ
   SET FAIL_CNT = #{failCnt}
 WHERE ID = #{id}
   AND USE_YN = 'Y'

mapper.select:
/* queryId = updateLoginUseYn */
UPDATE USER_INFO_MDJ
   SET USE_YN = #{useYn}
 WHERE ID = #{id}
