mapper.insert:
/* query Id = insertUserMng */
INSERT INTO 
USER_INFO_MDJ( ID
        , PW
        , NAME
        , NICKNAME
        , AGE
        , ADDRESS
        , EMAIL
        , USE_YN
        , FAIL_CNT
)VALUES(
 #{id}
,#{pw}
,#{name}
,#{nickname}
,#{age}
,#{address}
,#{email}
,#{useYn}
,#{failCnt}
)

mapper.update:
 /* query Id = updateUserMng */
UPDATE USER_INFO_MDJ 
   SET NAME     = #{name}
     , NICKNAME = #{nickname}
     , AGE      = #{age}
     , ADDRESS  = #{address}
     , EMAIL    = #{email}
     , USE_YN   = #{useYn}
     , FAIL_CNT  = #{failCnt}
WHERE ID = #{id}
  AND PW = #{pw}

/* query Id = updateUserMngDelYn */
 UPDATE USER_INFO_MDJ 
    SET USE_YN   = #{useYn}
  WHERE ID       = #{id}

/* query Id = deleteUserMngDel */
 DELETE USER_INFO_MDJ 
  WHERE ID       = #{id}
