mapper.select:
SELECT AUTH_CD -- 그룹코드 , AUTH_NM -- 그룹명 , REG_DT -- 등록일시 , REG_ID -- 등록자 , UPD_DT -- 수정일시 , UPD_ID -- 수정자 FROM AUTH_MST WHERE 1=1
AND AUTH_CD = #{authCd}
AND AUTH_NM = #{authNm}

mapper.insert:
INSERT INTO AUTH_DTL (
      AUTH_CD               -- 그룹코드
    , MENU_CD               -- 메뉴ID
    , USE_YN                -- 표시여부
    , CRT_YN                -- 생성권한
    , RED_YN                -- 읽기권한
    , SAV_YN                -- 저장권한
    , DEL_YN                -- 삭제권한
    , PRT_YN                -- 인쇄권한
    , EXL_YN                -- 엑셀권한
    , ETC_1_YN              -- 기타1권한
    , ETC_2_YN              -- 기타2권한
    , ETC_3_YN              -- 기타3권한
    , REG_DT                -- 등록일시
    , REG_ID                -- 등록자
    , UPD_DT                -- 수정일시
    , UPD_ID                -- 수정자
) VALUES (
      #{authCd}
    , #{menuCd}
    , #{useYn}
    , #{crtYn}
    , #{redYn}
    , #{savYn}
    , #{delYn}
    , #{prtYn}
    , #{exlYn}
    , #{etc1Yn}
    , #{etc2Yn}
    , #{etc3Yn}
    , #{regDt}
    , #{regId}
    , #{updDt}
    , #{updId}
)

mapper.delete:
DELETE FROM AUTH_DTL
       WHERE AUTH_CD = #{authCd}

         AND MENU_CD = #{menuCd}
