mapper.insert:
INSERT
  INTO CODE_MST
VALUES (
       #{clsCd}
     , #{clsNm}
     , #{remark}
     , CURRENT_DATE
     , #{id}
     , CURRENT_DATE
     , #{id}
     , 'Y'
)

INSERT
  INTO CODE_DTL
VALUES (
       #{codeCd}
     , #{clsCd}
     , #{codeNm}
     , #{opt1}
     , #{opt2}
     , #{opt3}
     , #{remark}
     , CURRENT_DATE
     , #{id}
     , CURRENT_DATE
     , #{id}
     , 'Y'
)

mapper.update:
UPDATE CODE_MST
   SET CLS_NM = #{clsNm}
     , REMARK = #{remark}
     , USE_YN = #{useYN}
     , UPD_DT = CURRENT_DATE
     , UPD_ID = #{id}
 WHERE 1=1
   AND CLS_CD = #{clsCd}

UPDATE CODE_DTL
   SET USE_YN = #{useYN}
 WHERE 1=1
   AND CLS_CD = #{clsCd}

UPDATE CODE_DTL
   SET CODE_NM = #{codeNm}
     , OPT_1   = #{opt1}
     , OPT_2   = #{opt2}
     , OPT_3   = #{opt3}
     , REMARK  = #{remark}
     , USE_YN  = #{useYN}
     , UPD_DT  = CURRENT_DATE
     , UPD_ID  = #{id}
 WHERE 1=1
   AND CLS_CD  = #{clsCd}
   AND CODE_CD = #{codeCd}
